frappe.ui.form.on("Delivery Note", {
  get_schemes: function(frm) {
      frappe.msgprint("<b>Schemes Available are updated</b>")
     frappe.call({
      method: "glosel.scheme_management.get_schemes",
      args: {"doc": frm.doc},
