});

frappe.ui.form.on("Schemes Available", "apply", function(frm,cdt,cdn) {
  var d=locals[cdt][cdn]
  frappe.call({
                 method: "frappe.client.get_list",
                 args: {
                     doctype: "Scheme Management Item",
                     fields: ["apply_on", "item_code"],
                     filters: { 'parent': d.scheme_name },
                 },
                    callback: function(res){
                       if (res && res.message){
                        if(d.scheme_type=="Fix"){
                            $.each(res.message,function(i,k){
                            var row = frappe.model.add_child(cur_frm.doc, "Schemes Selected", "schemes_selected");
                            row.scheme_name = d.scheme_name;
                            row.apply_on = d.apply_on;
                            row.scheme_type = d.scheme_type;
                            row.item_code=k.item_code;     
                            })   
                            frappe.msgprint("Free items added")                     
                        }
                        else if(d.scheme_type=="Selective" && d.apply_on=="Item Code"){
                          var row = frappe.model.add_child(cur_frm.doc, "Schemes Selected", "schemes_selected");
                            if(d.selected_item){
                              row.scheme_name = d.scheme_name;
                              row.apply_on = d.apply_on;
                              row.scheme_type = d.scheme_type;
                              row.item_code=d.selected_item;

                          }

                          else
                          { frappe.msgprint("Please select Item")}
                        }
                        else if(d.scheme_type=="Selective" && (d.apply_on=="Item Group" || d.apply_on=="Brand")){
                           // frappe.msgprint("coding..");
                           // frappe.msgprint(d.selected_item);
                           free_item_to_add = d.selected_item
                           free_item_to_add = free_item_to_add.split(",")
                              for(i=0;i<free_item_to_add.length-1;i++){
                                  var row = frappe.model.add_child(cur_frm.doc, "Schemes Selected", "schemes_selected");
                                    if(d.selected_item){
                                      row.scheme_name = d.scheme_name;
                                      row.apply_on = d.apply_on;
                                      row.scheme_type = d.scheme_type;
                                      row.item_code=free_item_to_add[i];                                        
                                    }
                                    else
                                    { 
                                      frappe.msgprint("Please select Item");
                                    }

                              }
                        }
                      }
                    }     
             }); 

});
frappe.ui.form.on("Schemes Available", "view_details", function(frm,cdt,cdn) {
  var d=locals[cdt][cdn]

  if(d.apply_on=="Brand"){
    frappe.call({
      method: "glosel.scheme_management.get_free_item_by_brand",
      args: {"doc": frm.doc, "apply_on":"Brand", "scheme_name":d.scheme_name},
      callback: function(r, rt){
            console.log(r.message,"jiiiiiiii");
            var len_custom = r.message.length;
            free_price = r.message[len_custom-1]["total_price"]
